arguments.argument:
if owner != -1 {
    if gun == WEAPON_MINEGUN owner.ammo[103]-=1;
    else  owner.ammo[101]-=1;

    if owner.weapon_index == WEAPON_MINEGUN && gun == WEAPON_MINEGUN owner.currentWeapon.lobbed -= 1;
    else if owner.weapon_index == WEAPON_SCOTTISHRESISTANCE && gun == WEAPON_SCOTTISHRESISTANCE owner.currentWeapon.lobbed -= 1;
}
