arguments.argument:
{
    if canActivate {
        with(TUMine) {
            if(ownerPlayer == other.ownerPlayer) && stickied {
                event_user(2);
                //other.lobbed -= 1;
                other.canActivate=false;
                other.alarm[4] = other.activateTime;
            }
        }
    }
    if canActivate {
        with(TUMine) {
            if(ownerPlayer == other.ownerPlayer) {
                event_user(2);
                //other.lobbed -= 1;
                other.canActivate=false;
                other.alarm[4] = other.activateTime;
            }
        }
    }
}
