arguments.argument:
if smashing {
    image_speed=0.3;
    if crit > 1 {
        if image_index >= 11{
            image_speed=0;
            image_index=8;
            stabbing = false;
        } 
    } else if image_index >= 4*owner.team+3 {
        image_speed=0;
        image_index=4*owner.team;
        stabbing = false;
    }    
} else {
    if crit <= 1  image_index=4*owner.team;
    else image_index = 8;
}

if !variable_local_exists('ammoCheck') {
    ammoCheck = 1;
    alarm[5] = owner.ammo[105];
}
