if(readyToShoot && !owner.cloak && ammoCount > 0 && !owner.cloak) {
    ammoCount-=1;
    playsound(x,y,RevolverSnd);
    var shot;
    randomize();

    shot = instance_create(x,y + yoffset + 1,DiplomatShot);
    shot.direction=owner.aimDirection+ random(2)-1;
    shot.speed=21;
    shot.crit = crit;
    shot.owner=owner;
    shot.ownerPlayer=ownerPlayer;
    shot.team=owner.team;
    shot.hitDamage = 20;
    shot.weapon=WEAPON_DIPLOMAT;
    with(shot)
        hspeed+=owner.hspeed;
    justShot=true;
    readyToShoot=false;
    alarm[0]=refireTime;
    alarm[5] = reloadBuffer + reloadTime;
} else if readyToStab && owner.cloak {
    stabdirection = owner.aimDirection;
    stab = instance_create(x,y,SapAnimation);
    stab.direction = owner.aimDirection;
    stab.owner = owner;
    stab.ownerPlayer = ownerPlayer;
    stab.team = owner.team;
    stab.hitDamage = 0;
    stab.weapon = WEAPON_SAPPER;
    readyToStab = false;
    owner.runPower = 0;
    owner.jumpStrength = 0;
    owner.stabbing = 1;
    alarm[1] = 20;
    stabbing=true;
